class Solution {
    bool canInterleave(int k, int i, int j, string& s1, string& s2, string& s3, vector<vector<vector<int>>>& dp) {

        // All indices reached the end then interleaving is possible
        if(i == s1.length() && j == s2.length() && k == s3.length())
            return true;

        if(dp[k][i][j] != -1)
            return dp[k][i][j];

        // Intially considering that interleaving is not possible
        bool res = false;

        // Check if we can match s1[i] to s3[k]
        // and if we can, can we get answer by matching them
        if(i < s1.length() && s1[i] == s3[k])
            res |= canInterleave(k + 1, i + 1, j, s1, s2, s3, dp);

        // Check if we can match s2[j] to s3[k]
        // and if we can, can we get answer by matching them
        if(j < s2.length() && s2[j] == s3[k])
            res |= canInterleave(k + 1, i, j + 1, s1, s2, s3, dp);

        return dp[k][i][j] = res;
    }

public:
