        if dp[x][y] != -1:
            return dp[x][y]
        a, b = False, False
        if x - 1 >= 0 and z - 1 >= 0 and s1[x - 1] == s3[z - 1]:
            a = self.solve(x - 1, y, z - 1, s1, s2, s3, dp)
        if y - 1 >= 0 and z - 1 >= 0 and s2[y - 1] == s3[z - 1]:
