class Solution {
    public boolean isInterleave(String s1, String s2, String s3) {
        Boolean[][][] memo=new Boolean[s1.length()+1][s2.length()+1][s3.length()+1];
        return helper(0,0,0,s1,s2,s3,memo);
    }
    public boolean helper(int i, int j, int k, String s1, String s2, String s3,Boolean[][][] memo){
        if(k>=s3.length() ) {
            if(i>=s1.length() && j>=s2.length() ) return true;
            return false;
        }

        if(i>=s1.length() && j>=s2.length()) return false;
        if(memo[i][j][k]!=null) return memo[i][j][k];
        boolean op1 = false;
        if(i<s1.length() && s1.charAt(i)==s3.charAt(k)){
            op1=helper(i+1,j,k+1,s1,s2,s3,memo);
        }
        boolean op2=false;
        if(j<s2.length()&& s2.charAt(j)==s3.charAt(k)){
            op2=helper(i,j+1,k+1,s1,s2,s3,memo);
        }
        return memo[i][j][k]= op1 || op2;
    }
}
