    int n1 = strlen(s1);
    int n2 = strlen(s2);
    int n3 = strlen(s3);
    if((n1 + n2) != n3)
        return false;
    if(n1 == 0){
        if(strcmp(s2, s3) == 0)
            return true;
        else
            return false;
    }
    if(n2 == 0){
        if(strcmp(s1, s3) == 0)
            return true;
        else
            return false;
    }
    if(s3[n3-1] != s1[n1-1]  &&  s3[n3-1] != s2[n2-1])
        return false;
    if(s3[0] != s1[0]  &&  s3[0] != s2[0])
        return false;

    bool** dp = malloc((n1+1) * sizeof(bool*));
    for(int i = 0; i <= n1; i++){
        dp[i] = calloc((n2+1), sizeof(bool));
    }

    dp[0][0] = true;
    for(int i = 1; i <= n1; i++){
        dp[i][0] = (dp[i-1][0]== true && (s1[i-1] == s3[i-1]));
    }
    for(int j = 1; j <= n2; j++){
        dp[0][j] = (dp[0][j-1]== true && (s2[j-1] == s3[j-1]));
