class Solution {

    //1. Top Down Approach
    bool SolveByMemo(string s1, string s2, string s3, vector<vector<int>> &dp)
    {
        int i = s1.length() , j = s2.length();

        if(dp[i][j] != -1)
            return dp[i][j];

        if(s3.empty())
            return dp[i][j] = (s1.empty() && s2.empty());

        if(s1.empty())
            return dp[i][j] = (s2 == s3);
        if(s2.empty())
            return dp[i][j] = (s1 == s3);

        if(s1[0] == s3[0] && s2[0] == s3[0])
            return dp[i][j] = SolveByMemo(s1.substr(1) , s2 , s3.substr(1) , dp) || SolveByMemo(s1 , s2.substr(1) , s3.substr(1), dp) ;
        if(s1[0] == s3[0] )
            return dp[i][j] = SolveByMemo(s1.substr(1) , s2 , s3.substr(1), dp);
        if(s2[0] == s3[0] )
            return dp[i][j] = SolveByMemo(s1 , s2.substr(1) , s3.substr(1), dp) ;

        return dp[i][j] = false;

    }

    // 2. Bottom UP Approach
    bool SolveByTab(string s1 , string s2 , string s3)
    {
         vector<vector<int>> dp(s1.length()+1, vector<int> (s2.length()+1, 0));

        for(int i = 0 ; i<= s1.length(); i++)
        {
            for(int j = 0; j<= s2.length() ; j++)
            {
                int k = i+j ;
                if(i == 0 && j == 0)
                   dp[0][0]  = 1;
                else if( i == 0)
                {
                    if(s2[j-1] == s3[k-1])
                        dp[i][j] = dp[i][j-1];
                }
                else if(j == 0)
                {
                    if(s1[i-1] == s3[k-1])
                        dp[i][j] = dp[i-1][j];
                }
                else
                {
                   dp[i][j] = (s1[i-1] == s3[k-1] && dp[i-1][j] || s2[j-1] == s3[k-1] && dp[i][j-1]);          
                }    
            }       
        }

        return dp[s1.length()][s2.length()];
    }

    // 3. Bottom Up Approach with Space Optimisation
      bool SolveSpaceOptimised(string s1 , string s2 , string s3)
    {
          if(s1.empty())
              return s2 == s3;
          if(s2.empty())
              return s1 == s3;

         vector<int> prev (s2.length()+1, 0);
         vector<int> curr (s2.length()+1, 0);

        for(int i = 0 ; i<= s1.length(); i++)
        {
            for(int j = 0; j<= s2.length() ; j++)
            {
                int k = i+j ;
                if(i == 0 && j == 0)
                  curr[0] = 1;
                else if( i == 0)
                {
                    if(s2[j-1] == s3[k-1])
                        curr[j] = curr[j-1];
                }
                else if(j == 0)
                {
                    if(s1[i-1] == s3[k-1])
                        curr[j] = prev[j];
                }
                else
                {
                   curr[j] = (s1[i-1] == s3[k-1] && prev[j] || s2[j-1] == s3[k-1] && curr[j-1]);
                }    
            }

            prev = curr;
        }

        return curr[s2.length()];
    }

public:
    bool isInterleave(string s1, string s2, string s3) {

        if(s1.length() + s2.length() != s3.length())
