class Solution {
public:
  int n1,n2,n3;
  set<pair<int,int>> st;                              //for memorization

  bool helper(string &s1, string &s2, string &s3, int i1, int i2, int i3){    
    if(st.count({i1,i2})) return false;                                       //if we already attend this variant 

    while(n1 != i1 || n2 != i2){

      if(n1 != i1 && n2 != i2 && s1[i1] == s2[i2] && s1[i1] == s3[i3]){       //if we have to use letters from two words
        st.insert({i1,i2});                                                   //check it situation
        return helper(s1, s2, s3, i1 + 1, i2 , i3 + 1) ||                     //and consider two possibilities
          helper(s1, s2, s3, i1 , i2 + 1 , i3 + 1);
