    public boolean isInterleave(String s1, String s2, String s3, int idx1, int idx2, int idx3) {

		//if we have reached the end of all the strings - found the answer
        if (idx3 == s3.length() && idx1 == s1.length() && idx2 == s2.length()) return true;

		//compare the character of s3 with s1
		//if same - recursively check for other characters with s3 index+1   and    s1 index+1
		//if not same compare the same character of s3 with s2
        if (idx1 < s1.length()  && idx3 < s3.length()  && s3.charAt(idx3) == s1.charAt(idx1)) {
            boolean result = isInterleave(s1, s2, s3, idx1 + 1, idx2, idx3 + 1);
            if (result) return true;
        }

		//compare the character of s3 with s2
		//if same - recursively check for other characters with s3 index+1   and    s2 index+1
		//if not same return false
        if (idx2 < s2.length() && idx3 < s3.length()  && s3.charAt(idx3) == s2.charAt(idx2)) {
            boolean result = isInterleave(s1, s2, s3, idx1, idx2 + 1, idx3 + 1);
            if (result) return true;
        }

        return false;
    }
}
