        if(s1.size() + s2.size() != s3.size())return false;
        vector<vector<vector<int>>>dp(s1.size()+ 1, vector<vector<int>>(s2.size()+1,vector<int>(s3.size()+1,-1)));
        return solve(s1,0,s2,0,s3,0,dp);
    }

};
