class Solution {
public:
    int largestPerimeter(vector<int>& nums) {

		// Sort in reverse order.
        sort(nums.begin(), nums.end(), greater<int>()); 

        int n = nums.size();
        int i = 0;

		// Run loop until we satisfy the basic triangle property ( sum of 2 sides >= third side).
		// As the array is sorted in reverse order we will always get the maximum perimeter.
        while (i < n - 2 && nums[i] >= nums[i + 1] + nums[i + 2]) {
            i++;
        }

		// If the we are unable to satisfy the property return 0 else the sum of 3 sides.
        return i >= n - 2 ? 0 : nums[i] + nums[i + 1] + nums[i + 2];
    }
};
