    private void setMaxAtIndexI(int index, int [] nums) {
        int maxValue = 0;
        int maxValueIndex = index;

        for (int i = 0; i <= index; i++) {
            if (maxValue < nums[i]) {
                maxValue = nums[i];
                maxValueIndex = i;
            }
        }
        nums[maxValueIndex] = nums[index];
        nums[index] = maxValue;
    }
}
