class Solution {
public:
    bool isValidBSTUtil(TreeNode* root, long long int maximum, long long int minimum){
        if(root == NULL)
            return true;
        if(root->val >= maximum || root->val <= minimum)
            return false;
        if(isValidBSTUtil(root->left, root->val, minimum) 
           && isValidBSTUtil(root->right, maximum, root->val)){
            return true;
