        inOrder(root->left, res);
        res.push_back(root->val);
        inOrder(root->right, res);
    }

    bool isValidBST(TreeNode* root) {
        if(root==NULL) return true;
        vector<int> res;
        inOrder(root, res);
        for(int i=0; i<res.size()-1; i++){
            if(res[i]>=res[i+1]) return false;
        }
        return true;
    }
};
