class Solution {
    public boolean isValidBST(TreeNode root) {
        int mainRoot = root.val;
        return isValidBSTUtil(root);
    }

    public boolean isValidBSTUtil(TreeNode root){
        if(root == null) return true;

        int rootVal = root.val;
        int leftVal = root.left != null?root.left.val: Integer.MIN_VALUE;
        int rightVal = root.right != null?root.right.val: Integer.MAX_VALUE;
        if(rootVal > leftVal && rootVal < rightVal){
            isValidBSTUtil(root.left);
            isValidBSTUtil(root.right);
        }
        else return false;

        return true;
    }
}

class Solution {
public:
    bool isValidBST(TreeNode* root,int max=INT_MAX,int min=INT_MIN) {
        if(root==NULL){
            return true;
        }
        if(root->val<min || root->val>max){
            return false;
        }
        int isleftok=isValidBST(root->left,root->val-1,min);
        int isrightok=isValidBST(root->right,max,root->val+1);
        return isleftok && isrightok;
    }
};


