class Solution {
public:
	int uniquePathsIII(vector<vector<int>>& grid) {
		int res = 0;
		// find starting positions
        for (int i = 0; i < grid.size(); i++){
            for (int j = 0; j < grid[0].size(); j++){
                if (grid[i][j] == 1){
					res = getRes(grid, i, j);
                }
            }
        }
        return res;
    }


    int getRes(vector<vector<int>>& grid, int i, int j){
		// check if we can move further
        if (i < 0 || i == grid.size() || j < 0 || j == grid[0].size() || grid[i][j] == -1){
            return 0;
        }
		// if we found ending square, check whether we visited all empty squares
        if (grid[i][j] == 2){
            for (int t = 0; t < grid.size(); t++){
                for (int k = 0; k < grid[0].size(); k++){
                    if (grid[t][k] == 0){
                        return 0;
                    }
                }
            }
            return 1;
        }
		// we have to mark current positions as visited, and later restore it
        auto temp = grid[i][j];
        grid[i][j] = -1;

		// run dfs for 4 directions
        int res = getRes(grid, i - 1, j);
        res += getRes(grid, i + 1, j);
        res += getRes(grid, i, j - 1);
        res += getRes(grid, i, j + 1);

		// restore position
        grid[i][j] = temp;

        return res;

    }
};
