    def uniquePathsIII(self, grid):
        N, M = len(grid), len(grid[0])

        full = 0
        for i, j in itertools.product(range(N), range(M)):
                if grid[i][j] != -1: full |= 1 <<(i*M+j)
                if grid[i][j] == 1: x, y = i, j
                elif grid[i][j] == 2: u, v = i, j

        dp = {(1<<(x*M+y),x,y): 1}

        bfs = set([(1<<(x*M+y),x,y)])
        while bfs:
            tmp = set()
            for mask, i, j in bfs:
                if (i, j) == (u, v): continue
                for a, b in [(i-1,j),(i+1,j),(i,j-1),(i,j+1)]:
                    if 0 <= a < N and 0 <= b < M and grid[a][b] != -1:
                        bit = 1 << (a*M + b)
                        if mask & bit: continue
                        newstate = (mask | bit, a, b)
                        dp[newstate] = dp.setdefault(newstate, 0) + dp[mask,i,j]
                        tmp.add(newstate)
            bfs = tmp
        return dp.setdefault((full,u,v), 0)
