class Solution {
vector<vector<int>> table;
int n,m;
inline bool isValidPath(){
	for(int i=0 ; i<n ; i++){
		for(int j=0 ; j<m ; j++)
			if(table[i][j]==0) return false;
	}
	return true;
}
inline bool isValidDir(int row , int col){
	return (row<n && row>=0 && col<m && col>=0 && (table[row][col]==0 || table[row][col]==2));
}
int dfs(int row , int col){
//if we are at end square just check weather all empy square is visited or not
//if all visited then the path by which we reached here is a valid path else not valid path
	if(table[row][col]==2) return isValidPath();
	table[row][col]=-1;//make it blocked 
	int ans=0;
	//explore all the possible direction
	if(isValidDir(row+1,col)){
		ans+=dfs(row+1,col);
	}
	if(isValidDir(row-1,col)){
		ans+=dfs(row-1 , col);
	}
	if(isValidDir(row , col+1)){
		ans+=dfs(row , col+1);
	}
	if(isValidDir(row , col-1)){
		ans+=dfs(row , col-1);
	}
	//while backtracking we are going to reset because the path which has given valid or invalide
	//path then partial path can be use for other's too
	table[row][col]=0;
	return ans;
}
