class Solution {
public:
    int ans=0;
    int empty=1;
    void path(vector<vector<int>>& grid, int i, int j, int c)
    {
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size() || grid[i][j]==-1)
        return;
        if(grid[i][j]==2)
        {
            if(c==empty)
            ans++;
            return;
        }
        grid[i][j]=-1;
        path(grid, i+1, j, c+1);
        path(grid, i, j+1, c+1);
        path(grid, i-1, j, c+1);
        path(grid, i, j-1, c+1);
        grid[i][j]=0;
    }
    int uniquePathsIII(vector<vector<int>>& grid) {
        int r, c;
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[0].size();j++)
            {
                if(grid[i][j]==0)
                empty++;
                if(grid[i][j]==1)
                r=i, c=j;
            }
        }
        path(grid, r, c, 0);
        return ans;
    }
};
