class Solution {
public:
    int n,m;
    int helper(vector<vector<int>>& grid, int i, int j, int cellsToVisit){
        if(i<0 || j<0 || i>=n || j>=m || grid[i][j]==-1 || grid[i][j]==3) 
            return 0;
        if(grid[i][j]==2){
            if(cellsToVisit==0)
                return 1;
            return 0;
        } 

        int ans = 0;
        // mark as visited
        grid[i][j] = 3;
        ans += helper(grid,i+1,j,cellsToVisit-1);
        ans += helper(grid,i,j+1,cellsToVisit-1);
        ans += helper(grid,i-1,j,cellsToVisit-1);
        ans += helper(grid,i,j-1,cellsToVisit-1);
        grid[i][j] = 0;

        return ans;
    }

    int uniquePathsIII(vector<vector<int>>& grid) {
        n = grid.size();
        m = grid[0].size();
