            for(int j=0;j<m;j++){
                if(grid[i][j]==1){
                    startx = i;
                    starty = j;
                }
                else if(grid[i][j] != -1) 
                    cellsToVisit++;
            }

        return helper(grid, startx, starty, cellsToVisit);
    }
};
