class Solution {

      int startrow = 0, startcol = 0, count = 0, ans  = 0;


    void dfs(vector<vector<int>>& grid, int i , int j , int cellvisited)
    {
        if(i<0 || j<0 || i>= grid.size() || j>= grid[0].size() || grid[i][j] == -1 )
            return ;

        // If You Have come to ending Position 
        if(grid[i][j] == 2 )
        {
            if(cellvisited == count)
                ans++;
            return ;
        }
        // Marking The Cell as Visited
        grid[i][j] = -1;

        // DFS Traversal in all possible direction
        dfs(grid, i+1 , j , cellvisited+1);
        dfs(grid, i-1 , j , cellvisited+1);
        dfs(grid, i , j+1 , cellvisited+1);
        dfs(grid, i , j-1 , cellvisited+1);

        // Backtracking
        grid[i][j] = 0;
