class Solution:
    def __init__(self):
        self.res = 0

    def uniquePathsIII(self, A):
        def dfs(x, y, empty):
            if 0 <= x < m and 0 <= y < n and A[x][y] >= 0:
                if A[x][y] == 2:
                    # destination reached, update count only if empty count is also zero, which implies that
                    # each empty cell has been explored
                    self.res += empty == 0
                    return
                A[x][y] = -2
                for (dx, dy) in directions:
                    dfs(x + dx, y + dy, empty - 1)
                A[x][y] = 0

        m, n, empty_counts = len(A), len(A[0]), 1
        start_x, start_y = None, None
        directions = [(1, 0), (-1, 0), (0, 1), (0, -1)]
        for i in range(m):
            for j in range(n):
                if A[i][j] == 1:
                    # start_node
                    start_x, start_y = (i, j)
                elif A[i][j] == 0:
                    empty_counts += 1

        dfs(start_x, start_y, empty_counts)
        return self.res

