class Solution {
    public int uniquePathsIII(int[][] grid) {
        if(grid==null || grid.length==0) return 0;

        int zero=0;
        int x=-1;
        int y=-1;

        for(int i=0;i<grid.length;i++){
            for(int j=0;j<grid[0].length;j++){
                if(grid[i][j]==0) zero++;
                else if(grid[i][j]==1){
                    x=i;
                    y=j;
                }
            }
        }
        return helper(grid,x,y,zero);
    }

    public int helper(int[][] grid, int x, int y, int zero){
        if(x<0 || y<0 || x>=grid.length || y>=grid[0].length || grid[x][y]==-1) return 0;

        if(grid[x][y]==2) return zero == -1 ? 1 : 0;

        grid[x][y]=-1;
        zero--;

        int ans = helper(grid,x+1,y,zero)
                + helper(grid,x-1,y,zero)
                + helper(grid,x,y+1,zero)
                + helper(grid,x,y-1,zero);

        zero++;
        grid[x][y]=0;

        return ans;
    }
}

