class Solution {
    int[][] directions = {{1,0}, {-1,0}, {0,1}, {0,-1}};

    public int uniquePathsIII(int[][] grid) {
        int rows = grid.length, cols = grid[0].length;
        int[] start = new int[2];
        int emptySquares = 0;

        for(int i=0; i<rows; i++){
            for(int j=0; j<cols; j++){
                if(grid[i][j] == 1){
                    start[0] = i;
                    start[1] = j;
                }else if(grid[i][j] == 0){
                    emptySquares++;
                }
            }
        }

        return countPaths(start[0], start[1], grid, emptySquares);
    }

    private int countPaths(int row, int col, int[][] grid, int emptySquares){
        if(row<0 || col<0 || row>=grid.length || col>=grid[0].length){
            return 0;
        }else if(grid[row][col] == -1 || grid[row][col] == 3){
            return 0;
        }else if(grid[row][col] == 2){
            return emptySquares == -1 ? 1 : 0;
        }

        grid[row][col] = 3;

        int countPaths = 0;

        for(int[] d : directions){
            countPaths += countPaths(row+d[0], col+d[1], grid, emptySquares-1);
        }

        grid[row][col] = 0;
        return countPaths;
    }
}
