class Solution {
public:
    int answer , countZeros , n , m;
    bool vis[30][30];

    bool valid(int i,int j){
        return (i>=0 and j>=0 and i<n and j<m);
    }

    void dfs(int i,int j,int ct0,vector<vector<int>>& grid){
        if(valid(i,j) == 0 or vis[i][j] or grid[i][j] == -1)
            return;
        vis[i][j] = 1;
        if(grid[i][j] == 0)
            ++ct0;
        if(ct0 == countZeros and grid[i][j] == 2)
            ++answer;

        dfs(i+1,j,ct0,grid);
        dfs(i,j+1,ct0,grid);
        dfs(i-1,j,ct0,grid);
        dfs(i,j-1,ct0,grid);

        if(grid[i][j] == 0)
            --ct0;
        vis[i][j] = 0;
    }

    int uniquePathsIII(vector<vector<int>>& grid) {
        answer = 0 , countZeros = 0;
        memset(vis,0,sizeof vis);
        for(auto it : grid){
            for(auto it2 : it)
                countZeros += (it2 == 0);
        }
        n = grid.size() , m = grid[0].size();

        int startx, starty , flag = 1;
        for(int i=0;i<n and flag;++i){
            for(int j=0;j<m and flag;++j){
                if(grid[i][j] == 1){
                    startx = i, starty = j;
                    flag = 0;
                    break;
                }
            }
        }
        dfs(startx,starty,0,grid);
        return answer;
    }
};
