```
class Solution {

    int solve(int[][]grid,int x, int y, int m, int n, int nonObstacles){
        if(x<0 || x>=m || y<0 || y>=n || grid[x][y]==-1 || grid[x][y]==1) return 0; //sanity check
        if(grid[x][y]==2){ //destination
            if(nonObstacles==0) return 1; // every non-obstacle square is visited (the only valid condition)
            return 0; //every non-obstacle square is not visited
        }
        if(nonObstacles==0) return 0; //without reaching the target all non-obstacle squares are visited  
        grid[x][y] = -1; //mark visited
        int way1 = solve(grid,x+1,y,m,n,nonObstacles-1);
        int way2 = solve(grid,x-1,y,m,n,nonObstacles-1);
        int way3 = solve(grid,x,y+1,m,n,nonObstacles-1);
        int way4 = solve(grid,x,y-1,m,n,nonObstacles-1);
        grid[x][y] = 0; //backtrack
        return way1+way2+way3+way4;
    }

    public int uniquePathsIII(int[][] grid){
        int m = grid.length;
        int n = grid[0].length;
        int nonObstacles = 0;

        for(int[] row : grid){ //count the number of obstacles that are needed to be visited before reaching the destination
            for(int i=0;i<row.length;i++) if(row[i]==0) nonObstacles++;
        }

        for(int x=0;x<m;x++){ //find the starting point 
            for(int y=0;y<n;y++){
                if(grid[x][y]==1){
                    return solve(grid,x+1,y,m,n,nonObstacles)+
                    solve(grid,x-1,y,m,n,nonObstacles)+
                    solve(grid,x,y+1,m,n,nonObstacles)+
                    solve(grid,x,y-1,m,n,nonObstacles);
                }
            }
        }
        return 0;
    }
}
