class Solution {

    private int w, h, totalCells;

    public int uniquePathsIII(int[][] grid) {
        this.w = grid.length;
        this.h = grid[0].length;

        int[] startCell = new int[2];
        int cellsToVisit = 0;

        for (int i = 0; i < w; i++) {
            for (int j = 0; j < h; j++) {
                if (grid[i][j] == 1) {
                    startCell[0] = i;
                    startCell[1] = j;
                } else if (grid[i][j] == 0) {
                    cellsToVisit++;
                }
            }
        }

        return dfs(grid, startCell[0], startCell[1], cellsToVisit+1, new HashSet<>());

    }

    public int dfs(int[][] grid, int i, int j, int cellsToVisit, Set<String> visited) {
        if ( i < 0||j < 0||i >= grid.length || j >= grid[i].length) {
            return 0;
        }

        if (grid[i][j] == -1 || visited.contains(i+" "+j)) {
            return 0;
        }


        if (grid[i][j] == 2) {// finishing cell 
            if(visited.size() == cellsToVisit) { // traversed all the cells without obstacles
                return 1;
            }

            return 0;
        }

        visited.add(i+" "+j);

        int pathsCount = 0;
        pathsCount += dfs(grid, i+1, j, cellsToVisit, new HashSet<>(visited));
        pathsCount += dfs(grid, i-1, j, cellsToVisit, new HashSet<>(visited));
        pathsCount += dfs(grid, i, j+1, cellsToVisit, new HashSet<>(visited));
        pathsCount += dfs(grid, i, j-1, cellsToVisit, new HashSet<>(visited));
        return pathsCount;
    }



}
