    public int uniquePathsIII(int[][] grid) {
        int blocks = 1;
        for(int[] arr:grid) for(int a:arr) if(a==0) blocks++;
        for(int i=0;i<grid.length;i++) for(int j=0;j<grid[0].length;j++) if(grid[i][j] == 1) dfs(grid,i,j,blocks);
