class Solution {
private:
    void helper(vector<vector<int>>& grid, bool**visited,int m,int n,int i,int j,int &count){


        visited[i][j]=true;

        if(grid[i][j]==2){
            for(int a=0;a<m;a++){
                for(int b=0;b<n;b++){
                    if(visited[a][b]==false){
                        visited[i][j]=false;
                        return;
                    }
                }
            }
            count++;
            visited[i][j]=false;
            return;
        }

        for(int x=i-1;x<=i+1;x++){
            if(x>=0 and x<m and visited[x][j]==false){
                helper(grid,visited,m,n,x,j,count);
            }
        }

         for(int y=j-1;y<=j+1;y++){
            if(y>=0 and y<n and visited[i][y]==false){
                helper(grid,visited,m,n,i,y,count);
            }
        }

        visited[i][j]=false;

    }
public:
