    void sub(vector<vector<int>> &mat , vector<vector<bool>> &vis, int si, int sj, int &mark)
    {
        if(si==end_i and sj==end_j)
        { 
            if(mark== n*m-ob-1) // total cell - no of obstacle - 1 , why -1 ? as we are not counting for endi,j
                ans++ ; 
            return;
        }

        for(int k=0 ; k<4 ; k++)
        {
            int newi = si+x[k]; // our new i and j
            int newj = sj+y[k];

            //we must look that newi,j is valid and not been visited and doesnot have obstacle
            if(newi>=0 and newi<n and newj>=0 and newj<m and mat[newi][newj]!=-1 and !vis[newi][newj])
            {
                vis[newi][newj]=true;
                sub(mat,vis,newi, newj, ++mark);
                vis[newi][newj]=false,mark--;          // backtracking step
            }
        }
    }

    int uniquePathsIII(vector<vector<int>>& mat) {        
        n = mat.size();
        m = mat[0].size();
        ans = ob = 0;
        int mark=0;
        for(int i=0 ; i<n ; i++)
