**Recursive Solution :**
```
class Solution {
public:
    int f(int i,int j,int c,int& empty,vector<vector<int>>& grid){
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size() || grid[i][j]==-1) return 0;
        if(grid[i][j]==2){
            if(c==empty)
                return 1;
            return 0;
        }
        grid[i][j]=-1;
        int up=f(i-1,j,c+1,empty,grid);
        int down=f(i+1,j,c+1,empty,grid);
        int right=f(i,j+1,c+1,empty,grid);
        int left=f(i,j-1,c+1,empty,grid);
        grid[i][j]=0;
        return up+down+right+left;        

    }

    int uniquePathsIII(vector<vector<int>>& grid) {
        int i=0,j=0,empty=1,start_x=0,start_y=0;
        for(i;i<grid.size();i++){
            for(j=0;j<grid[0].size();j++){
                if(grid[i][j]==1){
                    start_x=i;
                    start_y=j;                    
                }
                if(grid[i][j]==0)
                    empty++;
            }
        }
        return f(start_x,start_y,0,empty,grid);
    }
};
```

