class Solution {
public:
    int lol(int i,int j,int c,int& empty,vector<vector<int>>& grid,vector<vector<vector<int>>>& dp){
        //base case1
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size() || grid[i][j]==-1) return 0;        
        //base case2
        if(grid[i][j]==2){
            //sees if visited
            if(c==empty)
                return 1;
            return 0;
        }
        if(dp[i][j][c]!=-1)return dp[i][j][c];

        grid[i][j]=-1;
        //running down all 4 possibilities 
        int up=lol(i-1,j,c+1,empty,grid,dp);
        int down=lol(i+1,j,c+1,empty,grid,dp);
        int right=lol(i,j+1,c+1,empty,grid,dp);
        int left=lol(i,j-1,c+1,empty,grid,dp);
        //backtracking 
        grid[i][j]=0;
        //store answer in dp and return
        return dp[i][j][c+1]=(up+down+right+left);        

    }

    int uniquePathsIII(vector<vector<int>>& grid) {
        int empty=1,x=0,y=0;
        //empty is cells with 0
        //x and y are coordinates of starting point marked with 1 in grid
        for(int i=0;i<grid.size();i++){
            for(int j=0;j<grid[0].size();j++){
                if(grid[i][j]==1){
                    x=i;
                    y=j;                    
                }
                if(grid[i][j]==0)
                    empty++;
            }
        }
        vector<vector<vector<int>>> dp(grid.size(), vector<vector<int>> (grid[0].size(),vector<int> (empty+1,-1)));
        return lol(x,y,0,empty,grid,dp);
    }
};
