we have to traverse all the squares except obstacles, i.e -1
```
class Solution {
public:
    int startx;
    int starty;
    int endx;
    int endy;
    int ans=0;
    int squares_which_are_path;//squares which we have to travel
    int m;
    int n;
    vector<int> dx = {0,0,1,-1};
    vector<int> dy = {1,-1,0,0};
    void dfs(vector<vector<int>> &grid, int i, int j, int path)
    {
        //we have reached ending point i.e 2 after walking all squares
        if(i==endx && j==endy && path == squares_which_are_path)
        {
            ans++;
            return;
        }
        grid[i][j] = -1;//marking current as visited, so we don't calculate calculated values again

        for(int k=0; k<4; k++)
        {
            int x = i+dx[k];
            int y = j+dy[k];
            //we can walk all the paths excluding obstacles i.e -1
            if(x>=0 and x<m and y>=0 and y<n and grid[x][y] != -1)
                dfs(grid, x, y, path+1);  
        }
        grid[i][j] = 0;
    }
    int uniquePathsIII(vector<vector<int>>& grid) 
    {
        m = grid.size();
        n = grid[0].size();
        squares_which_are_path = m*n;//total if we also include -1
        for(int i=0; i<m; i++)
        {
            for(int j=0; j<n; j++)
            {
                if(grid[i][j] == 1)//our starting point
                {
                    startx = i;
                    starty = j;
                }
                else if(grid[i][j] == 2)//our ending point
                {
                    endx = i;
                    endy = j;
                }
                else if(grid[i][j] == -1)
                {
                    squares_which_are_path--;
                    //subtract as -1 will not form a path, it is obstacle
                }
            }
        }
        dfs(grid, startx, starty,1);
        return ans;
    }
};

