int traverse(int** grid, int row, int col, int rLimit, int cLimit, int spaces) {
    if(row < 0 || row > rLimit || col < 0 || col > cLimit || grid[row][col] == -1) return 0;
    if(grid[row][col] == 2) return (spaces == 0)? 1 : 0;

    int ans = 0;
    grid[row][col] = -1;
    ans += traverse(grid, row - 1, col, rLimit, cLimit, spaces - 1);
    ans += traverse(grid, row + 1, col, rLimit, cLimit, spaces - 1);
    ans += traverse(grid, row, col - 1, rLimit, cLimit, spaces - 1);
    ans += traverse(grid, row, col + 1, rLimit, cLimit, spaces - 1);
    grid[row][col] = 0;

    return ans;
}

int uniquePathsIII(int** grid, int gridSize, int* gridColSize){
    int startRow = 0, startCol = 0;
    int spaces = 1;
    int rLimit = gridSize - 1;
    int cLimit = *gridColSize - 1;
    for(int i = 0; i < gridSize; i++) {
        for(int j = 0; j < *gridColSize; j++) {
            if(grid[i][j] == 1) {
                startRow = i;
                startCol = j;
            }
            else if(!grid[i][j]) spaces++;
        }
    }

    return traverse(grid, startRow, startCol, rLimit, cLimit, spaces);
}
