class Solution {
public:
    pair<int,int> p[4] = {{1, 0}, {0, 1}, {-1, 0}, {0, -1}}; //path from each index
    int target_mask = 0; // each bit represent a cell 
    int ans= 0;
    void dfs(vector<vector<int>>& grid, int i, int j, int mask, int& n, int& m){
        if(grid[i][j] == 2) {
            if( (mask | (1<<(i*m + j))) == target_mask) //checking if we have visited all cells before target
            ans++;
            return;
        }
        for(auto a: p){
            int x = a.first + i, y = a.second + j;
            if(x < 0 or y < 0 or x >= n or y >= m or grid[x][y] == -1 or mask&(1<<(x*m + y))) continue;
            dfs(grid, x, y, mask | (1<<(x*m + y)), n, m); //masking the cell bit as visited while making dfs call
        }
    }
    int uniquePathsIII(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        target_mask = (1 << n*m) -1; //making every bit as visited for target mask 
        int row, col;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                if(grid[i][j] == 1){  // getting the starting index address
                    row = i;
                    col = j;
                }
                if(grid[i][j] == -1){
                    target_mask = target_mask^(1<<(i*m + j)); // changing the -1 cell's bit to 0
                }
            }
        }
        dfs(grid, row, col, (0 | (1<<(row*m + col))), n, m); //making the beginning node visited and making dfs call 
        return ans;
    }
};
