class Solution {
private:
    int dfs(vector<vector<int>> &grid, int x, int y, int zeros){
        if(x<0 || y<0 || x>=grid.size() || y>=grid[0].size() || grid[x][y] == -1){  // base cases
            return 0;
        }

        if(grid[x][y] == 2){
            // reaching required end
            return zeros == -1 ? 1 : 0;   //check if all zeros covered, then it's a possible path 
        }

        grid[x][y] = -1;        // marking as visisted 
        zeros--;

        int totalPath = dfs(grid, x + 1, y, zeros) +        // right 
                        dfs(grid, x, y + 1, zeros) +        // down  
                        dfs(grid, x - 1, y, zeros) +        // left 
                        dfs(grid, x, y - 1, zeros);         // top 

        grid[x][y] = 0;     // these steps are added 
        zeros++;            // to backtrack 

        return totalPath;
    }

public:
    int uniquePathsIII(vector<vector<int>>& grid) {
        int zeros = 0, sx = 0, sy = 0;      // sx & sy are starting positions
        for(int i=0; i<grid.size(); i++){
            for(int j=0; j<grid[0].size(); j++){
                if(grid[i][j] == 0) zeros++;
                else if (grid[i][j] == 1){
                    sx = i;
                    sy = j;
                } 
            }
        }

        return dfs(grid, sx, sy, zeros);
    }
};
