This problem is a slight modification of the `rat in a maze` problem, the only difference being: we have to find only those paths that cover every block (except the hurdles of course). So, a simple modification in the base case is, we have to return only that path for which all the blocks have been visited. (Now try again without looking at the code below.)
``` 
int getPaths(int i, int j, vector<vector<int>> &mat, int m, int n, vector<vector<int>> &visited, int* di, int* dj){
        if (i<0 || j<0 || i==m || j==n || visited[i][j]==1 || mat[i][j]==-1)
            return 0;

        if (mat[i][j]==2){  // base case (hit the target)
            for (int a=0;a<m;a++)  // checking if any block in the visited array is unvisited
                for (int b=0;b<n;b++)
                    if (mat[a][b]!=-1 && mat[a][b]!=2 && visited[a][b]==0)
                        return 0;
            return 1;
        }

        visited[i][j] = 1;
        int counter = 0;
        for (int k=0;k<4;k++)
            counter += getPaths(i+di[k], j+dj[k], mat, m, n, visited, di, dj);
        visited[i][j] = 0;
        return counter;
    }

    int uniquePathsIII(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<int>> visited(m, vector<int>(n, 0));
        int di[4] = {1, 0, -1, 0};
        int dj[4] = {0, 1, 0, -1};
        int i, j;
        bool flag = false;
        for (i=0; i<m && flag==false; i++)  // searching for the starting point (grid[i][j]=1)
            for (j=0;j<n;j++)
                if (grid[i][j]==1){
                    flag = true;
                    break;
                }
        return getPaths(--i, j, grid, m, n, visited, di, dj);  // --i to get the correct position of the starting point (due to the method used to break out of 2 for loops)
    }
