class Solution {
    int ans = 0;
    int totalEmpty = 1;

    public int uniquePathsIII(int[][] grid) {
        int row = -1;
        int col = -1;
        for(int i = 0; i < grid.length; i++){
            for(int j =0; j < grid[0].length; j++){
                if(grid[i][j] == 1){
                    row = i;
                    col = j;
                }else if(grid[i][j] == 0){
                    totalEmpty++;
                }
            }
        }

        dfs(grid, row, col);
        return ans;
    }

    public void dfs(int [][]grid, int r, int c){

        if(r < 0 || c <0 || r >= grid.length || c >= grid[0].length || grid[r][c] < 0)
            return;

        if(grid[r][c] == 2){
            if(totalEmpty == 0)
            ans += 1;
            return;
        }

        grid[r][c] = -2;
        totalEmpty--;
        dfs(grid, r+1,c);
        dfs(grid, r-1,c);
        dfs(grid, r,c+1);
        dfs(grid, r,c-1);
        grid[r][c] = 0;
        totalEmpty++;
    }
}

