    public int uniquePathsIII(int[][] grid) // Main method.
    {
       for(int i = 0; i < grid.length; i++) // The nested for loops are responsible for locating the starting point.
       {
           for(int j = 0; j < grid[0].length; j++)
           {
               if(grid[i][j] == 1) // When the starting point has been found, assigned the starting index x & y.
               {
                   startPointX = i;
                   startPointY = j;
               }
               if(grid[i][j] == 0) // When the value in a given spot are zero, count it.
               {
                   count++;
               }
           }
       }
          return helper(grid, startPointX, startPointY, count); 
    }

    private int helper(int[][] grid, int x, int y, int count) // Helper method.
    {

        if(x < 0 || grid.length-1 < x || y < 0 || grid[0].length-1 < y || grid[x][y] == -1) // Boundaries, visited spots and forbidden spots check.
        {
            return 0;
        }

        if(grid[x][y] == 2) // If we stumbled upon a spot with value of two, thats mean that we have finish the current iteration.
        {
            if(count == -1) // Check for valid count.
            {
                return 1;
            }
            else
            {
                return 0;
            }
        }
            int temp = grid[x][y]; // Copy a current spots value in order to not repeat it in the current iteration.
            grid[x][y] = -1; // Overwrite the current value white zero so that we will know that we have been here before.
            count--;

            int up = helper(grid, x-1, y,count);   // valid movements for traversing the matrix.
            int down = helper(grid, x+1, y, count);
            int left = helper(grid, x, y-1, count);
            int right = helper(grid, x, y+1,count);

            grid[x][y] = temp; // After traversing return the original value of a spot for the next iteration.
            count++; // count the iteration.
            return up + down + left + right; // return a valid movement. (backtracking keeps all movements of a current iteration untill the end of it).

