class Solution {
public:
    int f=0;
    void func(vector<vector<int>> grid,int i,int j,int c)
    {
        if(i<0 || j<0 || i>=grid.size() || j>=grid[0].size())
        {
            return;
        }
        if(grid[i][j]==-1 || grid[i][j]==3)
        {
            return;
        }
        if(grid[i][j]==2 && c==0)
        {
            f++;
            return;
        }
        if(grid[i][j]==2)
        {
            return;
        }
        grid[i][j]=3; /// mark the point as visited
        func(grid,i,j+1,c-1);
        func(grid,i+1,j,c-1);
        func(grid,i-1,j,c-1);
        func(grid,i,j-1,c-1);

