        int low = 0;
        int high = vec.size() - 1;

        while (low <= high) {
            int mid = low + (high - low) / 2;

            if (vec[mid].second > timestamp) {
                high = mid - 1;
            } else if (vec[mid].second < timestamp) {
                low = mid + 1;
            } else {
                return vec[mid].first;
            }
        }

        return high >= 0 ? vec[high].first : "";
    }
