class TimeMap {
public:
unordered_map<string, map<int, string>> time_map;

    /** Initialize your data structure here. */
    TimeMap() {

    }

    void set(string key, string value, int timestamp) {
        // use hash map to lookup ordered {timestamp, value} pairs by key in O(1).
        time_map[key].insert({ timestamp, value });
    }

    string get(string key, int timestamp) {
        auto exist = time_map.find(key) !=  time_map.end();
        if (!exist) {
            return "";
        }

        // use binary search to find the value with a timestamp greater the requested one.
        auto it = time_map[key].upper_bound(timestamp);
        auto minimum_it = time_map[key].begin();

        // if the first item with greater timestamp is equal to the first item in the time_map[key]
        // it means there is no value with a timestamp less than or equal to the requestd one 
        if (it == minimum_it) {
            return "";
        }

        // return an item with the largest timestamp
        return prev(it)->second;
    }
};
