class TimeMap {
public:
    unordered_map<string,string> mp;
    unordered_map<string,priority_queue<int>> mp2;

    TimeMap() {
        mp.clear();
        mp2.clear();
    }

    void set(string key, string value, int timestamp) {
        string s = key + "_" + to_string(timestamp);
        mp2[key].push(timestamp); // storing all the timestamps for same key in descending order
        mp[s] = value;
    }

    string get(string key, int timestamp) {
        string s = key + "_" + to_string(timestamp);
        if(!mp.count(s)){ // if (key + timestamp) doesn't found then find the largest timestamp which is less equal to the given timestamp 
            vector<int> vec;

            while(!mp2[key].empty() && mp2[key].top() > timestamp){
                vec.push_back(mp2[key].top());
                mp2[key].pop();
            }
            if(mp2[key].empty()){
                for(auto it:vec){
                    mp2[key].push(it);
                }
                return "";  
            } 
            string str = key + "_" + to_string(mp2[key].top());
            for(auto it:vec){
                mp2[key].push(it);
            }
            return mp[str];
        }
        return mp[s];
    }
};

