class TimeMap:

    def binarySearch(self, a, key):
        if key < a[0][1]:
            return ''
        elif key >= a[-1][1]:
            return a[-1][0]

        low = 0
        high = len(a) - 1

        while low <= high:
            mid = low + (high - low) // 2
            if a[mid][1] == key:
                return a[mid][0]
            elif a[mid][1] > key:
                high = mid - 1
            else:
                low = mid + 1
        return a[high][0]

