class TimeMapValue:
    def __init__(self, value: str, timestamp: int):
        self.values = {timestamp: value}
        self.max_timestamp = timestamp

    def set(self, value: str, timestamp: int) -> None:
        self.values[timestamp] = value
        if timestamp > self.max_timestamp:
            self.max_timestamp = timestamp

    def get(self, timestamp) -> str:
        if timestamp > self.max_timestamp:
            return self.values[self.max_timestamp]
        res = ""
        vals = list(self.values.keys())
        low, high = 0, len(vals) - 1
        while low <= high:
            mid = (low + high) // 2
            if vals[mid] <= timestamp:
                res = self.values[vals[mid]]
                low = mid + 1
            else:
                high = mid - 1

        return res

        if self.max_timestamp in self.values:
            return self.values[self.max_timestamp]

class TimeMap:

    def __init__(self):
        self.store = {}

    def set(self, key: str, value: str, timestamp: int) -> None:
        if key not in self.store:
            # Create it
            entry = TimeMapValue(value, timestamp)
            self.store[key] = entry
        else:
            # Update with additional key
            entry = self.store[key]
            entry.set(value, timestamp)

    def get(self, key: str, timestamp: int) -> str:
        if key in self.store:
            entry = self.store[key]
            return entry.get(timestamp)

        return ""



