class TimeMap {
public:
    TimeMap()
    {
    }
    void set(const string& key, const string& value, const int timestamp)
    {
        data_[key].emplace_back(timestamp, value);
    }
    string get(const string& key, const int timestamp)
    {
        const auto itData = data_.find(key);
        if (itData == data_.end())
        {
            return "";
        }
        const std::vector<std::pair<int, string>>& values = itData->second;

        const auto it = std::lower_bound(values.rbegin(), values.rend(), timestamp, 
        [&](const std::pair<int, string>& p, const int targeValue)
        {
            return p.first > targeValue;
        });
        if (it == values.rend())
        {
            return "";
        }
        return it->second;
    }
private:
    std::unordered_map<string, std::vector<std::pair<int, string>>> data_;

};
