class TimeMap {

    class ValueItem {
        String value;
        int time;
        public ValueItem(String value,int time) {
            this.value = value;
            this.time = time;
        }
    }

    HashMap<String,ArrayList<ValueItem>> dataStore;

    private String binSearch(ArrayList<ValueItem> arr,int reqTime) {

        int n = arr.size();
        int lo = 0,hi = n-1;
        String valueFound = "";
        while(lo<=hi) {

            int mid = lo+(hi-lo)/2;

            ValueItem currVal = arr.get(mid);

            if(reqTime==currVal.time) {
                return currVal.value;
            }
            else if(currVal.time<reqTime) {
                valueFound = currVal.value;
                lo = mid+1;
            } else {
                hi = mid-1;
            }
        }

        return valueFound;
    }

    public TimeMap() {
        dataStore = new HashMap<String,ArrayList<ValueItem>>();
    }

    public void set(String key, String value, int timestamp) {
        if(!dataStore.containsKey(key)) {
            dataStore.put(key,new ArrayList<ValueItem>());
        }

        dataStore.get(key).add(new ValueItem(value,timestamp));
    }

    public String get(String key, int timestamp) {
        if(!dataStore.containsKey(key)) {
            return "";
        } 
        return binSearch(dataStore.get(key),timestamp);
    }
}

