class TimeMap {

    Map<String, TreeMap<Integer, String>> map;

    public TimeMap() {
        map = new HashMap<>();
    }

    public void set(String key, String value, int timestamp) {
        map.putIfAbsent(key, new TreeMap<Integer, String>());
        map.get(key).put(timestamp, value);
    }

    public String get(String key, int timestamp) {
        if(!map.containsKey(key)) return "";
        /* floorEntry(K key) : Returns a key-value mapping associated with 
            the greatest key <= the given key, or null if there is no such key. */
        var tm = map.get(key).floorEntry(timestamp);
        return tm == null ? "" : tm.getValue();
    }
}
