        TreeMap<Integer, String> keyMap = map.get(key);
        if (keyMap == null) return "";
        Map.Entry<Integer,String> entry = keyMap.floorEntry(timestamp);
        if (entry == null) return "";
        return entry.getValue();
    }
}
