```kotlin
class TimeMap {

    private val dataSet = mutableMapOf<String, MutableList<TimeNode>>()

	// Time complexity: O(1)
	// Space complexity: O(1)
    fun set(key: String, value: String, timestamp: Int) {
        val nodes = dataSet.getOrPut(key) { mutableListOf() }

		// We do not allow entries that are in the past.
        val lastTimestamp: Int = nodes.lastOrNull()?.timestamp ?: Int.MIN_VALUE
        if (lastTimestamp < timestamp) {
            nodes += TimeNode(value, timestamp)
        }
    }

	// Time complexity: O(logn)
	// Space complexity: O(1)
    fun get(key: String, timestamp: Int): String {
        val nodes = dataSet.getOrPut(key) { mutableListOf() }

		// Short circuit: If nodes are empty, there is no message to verify.
        if (nodes.isEmpty()) return ""

		// We will need to keep track of the "most close" entry (see below).
        var node: TimeNode? = null

        var left = 0
        var right = nodes.size

		// Binary Search.
        while (left < right) {
            val mid = left + (right - left) / 2
            node = nodes[mid]

			// If we find a perfect match, return it.
            if (node.timestamp == timestamp) {
                return node.text
            }

			// Otherwise, continue the binary search.
            if (node.timestamp < timestamp) {
                left = mid + 1
            } else if (node.timestamp >= timestamp) {
                right = mid
            }
        }

		// If no match is found, we verify if the "most close" node is before the requested timestamp.
		// If yes, we can result it as that is the first message before the requested timestamp.
        if (node != null && node.timestamp <= timestamp) {
            return node.text
        }

		// If nothing is found, return empty string.
        return ""
    }
}

// Helper class, to keep the text/timestamp combination in our dataSet map.
private class TimeNode(
    val text: String,
    val timestamp: Int
)
