class Solution {
    Integer dp[][]=new Integer[3][1<<16];
    int find(int nums[],int num,int count){
        if(count==2)return num==0 ? 1 : 0;
        if(count>2)return 0;
        if(dp[count][num]!=null)return dp[count][num];
        int res=0;
        for(int i=0;i<nums.length;i++)
            res+=find(nums,num&nums[i],count+1);
         return dp[count][num]=res;        
    }

    public int countTriplets(int[] nums) {
        int count=0;
        for(int num : nums)count+=find(nums,num,0);
        return count;        
    }
}
